package day5;

public class WhileVsDoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub'
		//while loop
		/*
		 * int i = 10; while(i<=5) { System.out.println(i); i++; }
		 */
		//do while loop
		int i = 10;
		do {
			System.out.println(i);
			i++;
		}while(i<=5);
	}

}
